import java.util.Random;import java.util.Scanner;public class BinaryMatrix {    public static void main(String[] args) {        System.out.println("Enter size of matrix: ");        Scanner scanner = new Scanner(System.in);        int size = scanner.nextInt();        System.out.println("Matrix with size is " + size);        displayMatrix(size);    }    public static void displayMatrix(int size) {        Random random = new Random();        for (int i = 0; i < size; i++) {            for (int j = 0; j < size; j++) {                int rd = random.nextInt(2);                System.out.print(rd + " ");            }            System.out.println();        }    }}